function removePopup() {
    const popupContainer = document.getElementById('popup_container');
    if (popupContainer) {
        popupContainer.style.animation = 'scaleDown 0.5s forwards';
        popupContainer.addEventListener('animationend', () => {
            popupContainer.remove();
        });
    }
}
